const fetch = require('./src/fetch');

const config = require('./src/config');

module.exports.setToken = token => {
  config.setToken(token);
};

module.exports.getFundamentals = symbol => {
  const path = "/getFundamentals/".concat(symbol);
  return fetch(path);
};

module.exports.getHistoricalEodData = symbol => {
  const path = "/getHistoricalEodData/".concat(symbol);
  return fetch(path);
};

module.exports.getHistoricalEodData = ({
  symbol,
  from,
  to,
  period,
  order,
  filter
}) => {
  const path = "/getHistoricalEodData/".concat(symbol, "?fmt=json") + (from ? "&from=".concat(from) : '') + (to ? "&to=".concat(to) : '') + (period ? "&period=".concat(period) : '') + (order ? "&order=".concat(order) : '') + (filter ? "&filter=".concat(filter) : '');
  return fetch(path);
};

module.exports.listSupportedEtfs = () => {
  const path = "/listSupportedEtfs";
  return fetch(path);
};
